<template>
    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">About Me Banner</label>
        <div class="col-sm-9">
            <input type="file" class="form-control"  @change="(e) => { state.form.bannerAboutMe = e.target.files[0]; }" accept="image/webp" />
            <a-image :src="pathUrl + '/storage/' + state.form.bannerAboutMe" width="32" 
            fallback="data:image/png;base64,"
            />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">About Me Text Color</label>
        <div class="col-sm-9">
            <input type="color" class="form-control form-control-color" v-model="state.form.colorAboutMe" />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">About Me Content</label>
        <div class="col-sm-9">
            <ckeditor :editor="editor" v-model="state.form.aboutme" :config="editorConfig" />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Image 1 About Me</label>
        <div class="col-sm-9">
            <input type="file" class="form-control"  @change="(e) => { state.form.image1aboutme = e.target.files[0]; }" accept="image/webp" />
            <a-image :src="pathUrl + '/storage/' + state.form.image1aboutme" width="32" 
                fallback="data:image/png;base64,"
            />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Image 2 About Me</label>
        <div class="col-sm-9">
            <input type="file" class="form-control"  @change="(e) => { state.form.image2aboutme = e.target.files[0]; }" accept="image/webp" />
            <a-image :src="pathUrl + '/storage/' + state.form.image2aboutme" width="32" 
                fallback="data:image/png;base64,"
            />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Image 3 About Me</label>
        <div class="col-sm-9">
            <input type="file" class="form-control"  @change="(e) => { state.form.image3aboutme = e.target.files[0]; }" accept="image/webp" />
            <a-image :src="pathUrl + '/storage/' + state.form.image3aboutme" width="32" 
                fallback="data:image/png;base64,"
            />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Visi</label>
        <div class="col-sm-9">
            <ckeditor :editor="editor" v-model="state.form.visi" :config="editorConfig" />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Misi</label>
        <div class="col-sm-9">
            <ckeditor :editor="editor" v-model="state.form.misi" :config="editorConfig" />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Section Title About Me</label>
        <div class="col-sm-9">
            <input type="text" class="form-control" v-model="state.form.sectionTitleAboutme" />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Description Title About Me</label>
        <div class="col-sm-9">
            <ckeditor :editor="editor" v-model="state.form.descTitleAboutme" :config="editorConfig" />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Image Section About Me</label>
        <div class="col-sm-9">
            <input type="file" class="form-control"  @change="(e) => { state.form.imageSectionAboutme = e.target.files[0]; }" accept="image/webp" />
            <a-image :src="pathUrl + '/storage/' + state.form.imageSectionAboutme" width="32" 
                    fallback="data:image/png;base64,"
            />
        </div>
    </div>

    <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Background Section About Me</label>
        <div class="col-sm-9">
            <input type="file" class="form-control"  @change="(e) => { state.form.bgSectionAboutme = e.target.files[0]; }" accept="image/webp" />
            <a-image :src="pathUrl + '/storage/' + state.form.bgSectionAboutme" width="32" 
                fallback="data:image/png;base64,"
            />
        </div>
    </div>

    <div class="mb-3 row">
        <Mitra />
    </div>

    <div class="d-flex justify-content-end">
        <Button label="Save Settings" class="p-button-primary btn btn-dark mt-3" @click="save" :disabled="loadingSubmit"></Button>
    </div>
    <ProgressBar mode="indeterminate" style="height: 6px" v-if="loadingSubmit"></ProgressBar>
</template>

<script setup>
    import { apiGetData, apiPostData, processing, loadingButton, loadingSubmit, dayjs , Swal, waitingicon, loading, pesan } from '@/store/action';
    import { reactive, onMounted, ref } from 'vue';
    import Button from 'primevue/button';
    import ProgressBar from 'primevue/progressbar';
    import CKEditor from '@ckeditor/ckeditor5-vue';
    import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
    import Mitra from './mitra.vue';
    const ckeditor = CKEditor.component
    const editor = ClassicEditor;
    const editorConfig = {
        list: {
            properties: {
                styles: true,
                startIndex: true,
                reversed: true
            }
        },
        toolbar: [
            'heading', '|',
            'bold', 'italic', 'link', 'bulletedList', 'numberedList', 'blockQuote', '|',
            'fontSize', // tambahkan ini
            'undo', 'redo'
        ],
        fontSize: {
            options: [
                9,
                11,
                13,
                'default',
                17,
                19,
                21,
                27,
                35
            ],
            supportAllValues: false
        }
    };

    const pathUrl = import.meta.env.VITE_PATH_FILE_BASE_URL;
    const state = reactive({
        form: {
            odata: '',
            bannerAboutMe: '',
            colorAboutMe: '#000000',
            aboutme: '',
            image1aboutme: '',
            image2aboutme: '',
            image3aboutme: '',
            visi:'',
            misi:'',
            sectionTitleAboutme:'',
            descTitleAboutme:'',
            imageSectionAboutme:'',
            bgSectionAboutme:''
        }
    });

    const getData = async () => {
        loading.value = true;
        const response = await apiGetData('/setting/site_setting', {});
        state.form = response.data[0];
        state.form.odata = response.data[0].odata;
        loading.value = false;
    };

    const save = async () => {
        processing.value = true;
        pesan.value = 'Mohon tunggu, sedang menyimpan data...';
        const formData = new FormData();
        formData.append('odata', state.form.odata);
        formData.append('colorAboutMe', state.form.colorAboutMe);
        formData.append('aboutme', state.form.aboutme);
        formData.append('visi', state.form.visi);
        formData.append('misi', state.form.misi);
        formData.append('sectionTitleAboutme', state.form.sectionTitleAboutme);
        formData.append('descTitleAboutme', state.form.descTitleAboutme);

        if (state.form.bannerAboutMe instanceof File) {
            formData.append('bannerAboutMe', state.form.bannerAboutMe);
        }

        if (state.form.image1aboutme instanceof File) {
            formData.append('image1aboutme', state.form.image1aboutme);
        }

        if (state.form.image2aboutme instanceof File) {
            formData.append('image2aboutme', state.form.image2aboutme);
        }

        if (state.form.image3aboutme instanceof File) {
            formData.append('image3aboutme', state.form.image3aboutme);
        }

        if (state.form.imageSectionAboutme instanceof File) {
            formData.append('imageSectionAboutme', state.form.imageSectionAboutme);
        }

        if (state.form.bgSectionAboutme instanceof File) {
            formData.append('bgSectionAboutme', state.form.bgSectionAboutme);
        }

        const response = await apiPostData('/setting/site_setting_about_me', formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        });
        
        if(response){
            Swal.fire({
                icon: 'success',
                title: 'Success',
                text: 'Settings have been saved successfully.',
                confirmButtonColor: '#2c323f',
                confirmButtonText: '<i class="fa fa-check me-2"></i> OKE',
            });
            await getData();
            processing.value = false;
        } else{
            processing.value = false;
        }
    };


    onMounted(async () => {
        await getData();
    });


</script>

<style scoped>
    ::v-deep(.ck-content) {
        min-height: 300px;
        max-height: 300px;
        overflow-y: auto;
    }
</style>