<template>
    <Accordion value="0">
        <AccordionPanel value="0">
            <AccordionHeader>Contact Me Settings</AccordionHeader>
            <AccordionContent>  

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Contact Me Banner</label>
                    <div class="col-sm-9">
                        <input type="file" class="form-control" @change="onFileChange" accept="image/webp" />
                        <a-image :src="pathUrl + '/storage/' + state.contactme.bannerContactMe" width="32" 
                        fallback="data:image/png;base64,"
                        />
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Contact Me Image</label>
                    <div class="col-sm-9">
                        <input type="file" class="form-control" @change="(e) => { state.contactme.imageContactMe = e.target.files[0]; }" accept="image/webp" />
                        <a-image :src="pathUrl + '/storage/' + state.contactme.imageContactMe" width="32" 
                        fallback="data:image/png;base64,"
                        />
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Contact Me Form</label>
                    <div class="col-sm-9">
                        <input type="file" class="form-control" @change="(e) => { state.contactme.imageForm = e.target.files[0]; }" accept="image/webp" />
                        <a-image :src="pathUrl + '/storage/' + state.contactme.imageForm" width="32" 
                        fallback="data:image/png;base64,"
                        />
                    </div>
                </div>


                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Contact Me Text Color</label>
                    <div class="col-sm-9">
                        <input type="color" class="form-control form-control-color" v-model="state.contactme.colorContactMe" />
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Address</label>
                    <div class="col-sm-9">
                        <ckeditor :editor="editor" v-model="state.contactme.alamat" :config="editorConfig" />
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Operating Hours</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.jam" />
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Customer Service</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.cs" />
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Department Contact</label>
                    <div class="col-sm-9">
                        <ckeditor :editor="editor" v-model="state.contactme.deptContact" :config="editorConfig" />
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Email</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.email"></input>
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Email 2</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.email1"></input>
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Email 3</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.email2"></input>
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Phone</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.phone"></input>
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Phone 2</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.phone1"></input>
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">WhatsApp</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.wa"></input>
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">WhatsApp 2</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.wa1"></input>
                    </div>
                </div>

                <div class="mb-3 row">
                    <label class="col-sm-3 col-form-label">Google Maps Embed Code</label>
                    <div class="col-sm-9">
                        <input type="text" class="form-control" v-model="state.contactme.maps"></input>
                    </div>
                </div>

                <div class="d-flex justify-content-end">
                    <Button label="Save Settings" class="p-button-primary btn btn-dark mt-3" @click="save" :disabled="loadingSubmit"></Button>
                </div>
                <ProgressBar mode="indeterminate" style="height: 6px" v-if="loadingSubmit"></ProgressBar>
            </AccordionContent>
        </AccordionPanel>

        <AccordionPanel value="1">
            <AccordionHeader>Social Media Settings</AccordionHeader>
            <AccordionContent>
                <socials />
            </AccordionContent>
        </AccordionPanel>
    </Accordion>
</template>

<script setup>
    import { apiGetData, apiPostData, processing, loadingButton, loadingSubmit, dayjs , Swal, waitingicon, loading, pesan } from '@/store/action';
    import { reactive, onMounted, ref } from 'vue';
    import Button from 'primevue/button';
    import ProgressBar from 'primevue/progressbar';
    import socials from './socials.vue';
    import Accordion from 'primevue/accordion';
    import AccordionPanel from 'primevue/accordionpanel';
    import AccordionHeader from 'primevue/accordionheader';
    import AccordionContent from 'primevue/accordioncontent';
    import CKEditor from '@ckeditor/ckeditor5-vue';
    import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
    const ckeditor = CKEditor.component
    const editor = ClassicEditor;
    const editorConfig = {
        list: {
            properties: {
                styles: true,
                startIndex: true,
                reversed: true
            }
        },
        toolbar: [
            'heading', '|',
            'bold', 'italic', 'link', 'bulletedList', 'numberedList', 'blockQuote', '|',
            'fontSize', // tambahkan ini
            'undo', 'redo'
        ],
        fontSize: {
            options: [
                9,
                11,
                13,
                'default',
                17,
                19,
                21,
                27,
                35
            ],
            supportAllValues: false
        }
    };

    const pathUrl = import.meta.env.VITE_PATH_FILE_BASE_URL;


    const state = reactive({
        contactme: {
            odata: '',
            bannerContactMe: '',
            colorContactMe: '',
            alamat: '',
            jam: '',
            cs: '',
            maps: '',
            deptContact:'',
            imageContactMe: '',
            imageForm : '',
            email: '',
            email1: '',
            email2: '',
            phone: '',
            phone1: '',
            wa: '',
            wa1: '',
        }
    });

    const getData = async () => {
        loading.value = true;
        const response = await apiGetData('/setting/site_setting', {});
        state.contactme = response.data[0];
        state.contactme.odata = response.data[0].odata;
        loading.value = false;
    };

    const onFileChange = (event) => {
        const file = event.target.files[0];
        state.contactme.bannerContactMe = file;
    };

    const save = async () => {
        processing.value = true;
        pesan.value = 'Mohon tunggu, sedang menyimpan data...';
        const formData = new FormData();
        formData.append('odata', state.contactme.odata);
        formData.append('colorContactMe', state.contactme.colorContactMe);
        formData.append('alamat', state.contactme.alamat);  
        formData.append('jam', state.contactme.jam);
        formData.append('cs', state.contactme.cs);
        formData.append('maps', state.contactme.maps);
        formData.append('deptContact', state.contactme.deptContact);
        formData.append('email', state.contactme.email);
        formData.append('email1', state.contactme.email1);
        formData.append('email2', state.contactme.email2);
        formData.append('phone', state.contactme.phone);
        formData.append('phone1', state.contactme.phone1);
        formData.append('wa', state.contactme.wa);
        formData.append('wa1', state.contactme.wa1);

        if (state.contactme.bannerContactMe instanceof File) {
            formData.append('bannerContactMe', state.contactme.bannerContactMe);
        }

        if (state.contactme.imageContactMe instanceof File) {
            formData.append('imageContactMe', state.contactme.imageContactMe);
        }

        if (state.contactme.imageForm instanceof File) {
            formData.append('imageForm', state.contactme.imageForm);
        }

        const response = await apiPostData('/setting/site_setting_contact_me', formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        });
        
        if(response){
            Swal.fire({
                icon: 'success',
                title: 'Success',
                text: 'Settings have been saved successfully.',
                confirmButtonColor: '#2c323f',
                confirmButtonText: '<i class="fa fa-check me-2"></i> OKE',
            });
            await getData();
            processing.value = false;
        } else{
            processing.value = false;
        }
    };


    onMounted(async () => {
        await getData();
    });
</script>

<style scoped>
    ::v-deep(.ck-content) {
        min-height: 100px;
        max-height: 100px;
        overflow-y: auto;
    }
</style>